import React, { useEffect, useState } from "react";
import "./App.css";

function App() {
  const [neighborhoods, setNeighborhoods] = useState([]);
  const [query, setQuery] = useState("");

  useEffect(() => {
    fetch("http://localhost:5000/api/neighborhoods")
      .then(res => res.json())
      .then(data => setNeighborhoods(data));
  }, []);

  const filtered = neighborhoods.filter(n =>
    n.lifestyle.toLowerCase().includes(query.toLowerCase()) ||
    n.name.toLowerCase().includes(query.toLowerCase())
  );

  return (
    <div className="App">
      <h1>NeighborFit</h1>
      <p>Find a neighborhood that fits your lifestyle!</p>
      <input
        type="text"
        placeholder="Type a lifestyle or neighborhood..."
        value={query}
        onChange={e => setQuery(e.target.value)}
        className="search-box"
      />
      <h2>Neighborhoods</h2>
      <ul>
        {filtered.length === 0 ? (
          <li>No matches found.</li>
        ) : (
          filtered.map(n => (
            <li key={n.id}>
              <strong>{n.name}</strong>: {n.lifestyle}
            </li>
          ))
        )}
      </ul>
    </div>
  );
}

export default App;
